import React from 'react'
import '../../assets/styles/form.scss'
import Button from '../../components/Button/index.jsx'
import Input from '../../components/Input/index.jsx'
import Error from '../../components/Error/index.jsx'
import { FaBeer } from 'react-icons/fa';
import { Link } from 'react-router-dom'
import ValidateField from '../../utils/helpers/ValidateField';
import request from 'request';
import '../../components/RadioButton/index.jsx'

class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            fields: {
                email:'',
                password:''
            },
            errors: {
                isValidForm:false
            },
            ToggleOff: true,
        }
        this.handleToggle = this.handleToggle.bind(this)
    }
    handleToggle() {
        if (this.state.ToggleOff) {
            this.setState({ ToggleOff: false });
        }
        else {
            this.setState({ ToggleOff: true })
        }

    }
    handleUserInput(e) {
        const name = e.target.name;
        const value = e.target.value;
        let fields = this.state.fields;
        fields[name] = value;
        this.setState({ fields })
        this.setState({ [name]: value })
        let newState = JSON.parse(JSON.stringify(this.state.errors))
        //make changes to ingredients
        newState.email = undefined
        newState.password = undefined
        this.setState({
            errors: newState
        })
    }

    handleSubmit(e) {
        e.preventDefault()
        const error = ValidateField(this.state.fields)
        this.setState({ errors: error })
        console.log(error);

        if(error['isValidForm']){
            request({
                url: "http://localhost:4000/login",
                method: "POST",
                json: true,
                body:this.state.fields
            }, (error, response) => {
            let errors ={}
            if(response.statusCode===400)
                  {
                      console.log(response);

                    errors["email"]=`${response.body.message.error}`;
                    this.setState({errors})
                  }else if(response.body.message.success){
                      console.log("SuccessFully logged in"); 
                  }


            })
        } 
    }
    render() {
        return (
            <div className="formbox">
                <form className="demo-form"
                    noValidate
                    onSubmit={(e) => this.handleSubmit(e)}>
                    <div className='form-header'>
                        <h2 className='form-heading'>SIGN IN PAGE</h2>
                        <div className="form-descp">
                            <p className=''>Enter you email and password to continue</p>
                            <p className=''>To reset password click on forgot password field</p>
                        </div>
                    </div>
                    <div className="input-fields">
                        <Input
                            label={'Email'}
                            name={'email'}
                            type={'text'}
                            icon={false}
                            value={this.state.email}
                            onChange={(e) => this.handleUserInput(e)}
                            className={'normal'} />
                        <Input 
                            label={'Password'}
                            name={'password'}
                            type={'password'}
                            icon={true}
                            iconName={FaBeer}
                            toggleCondition={this.state.ToggleOff}
                            onClick={this.handleToggle}
                            value={this.state.password}
                            onChange={(e) => this.handleUserInput(e)}
                        />
                        <div className='remember_error_container'>
                           <div  className='remember_forgot_panel'>
                           <div>
                                <input type='checkbox' />
                                <span>Remember Me</span>
                            </div>
                            <Link to='forgotpassword'>
                                Forgot Password
                            </Link>
                           </div>
                            <div className="error">
                          {(this.state.errors.email || this.state.errors.password)  &&
                          <Error errorMessage={`${this.state.errors.email} ${this.state.errors.password}`} className={"warning"} />
                        }
                      </div>
                        </div>
                        </div>
                    <div className="fade-in">
                        <div className='buttonwrapper'>
                            <Button buttonName={"LOGIN"} className={"primary"} />
                        </div>
                    </div>
                </form>
            </div>
        )
    }
}

export default Form
